Trigger.script:
if statusFollowing then send("bclear") end if huntWait == nil then huntWait = 0 end direction = matches[2] if auto_hunt then
if not combat then local killlist = ""
if killTarget ~= "" then local killTargetTable = string.split(killTarget,",")
for key,value in pairs(killTargetTable) do killlist = killlist .. "kill " .. value .. ";" end end tempTimer((huntWait + 0.3), function () if not combat then send(direction..";" .. killlist .. (xaos.config.scripting.scan_mode and "scan;" or "") .. "hunt "..huntTarget) end end)
end end hunting = true

enemyAtRange = enemyAtRange or 0 enemyAtRange = enemyAtRange + 1 -- xuicom("Enemies found "..enemyAtRange) thisTriggerShot = thisTriggerShot or false
if not combat and enemyAtRange > 1 and guerrillaMode and not thisTriggerShot then send("bclear;cast lightning "..matches[2].." el;cast lightning "..matches[2].." el")
lastSpell = "cast lightning "..matches[2].." el" thisTriggerShot = true tempTimer(0.5, [[thisTriggerShot = false]])
end enemyCountTimer = tempTimer(0.5, [[enemyAtRange = 0]])

local comp = string.trim(matches[2]) local kill = string.trim(matches[3]) == "Kill" local name = fixstring(string.trim(matches[4])) local curcnt = string.trim(matches[5]) local maxcnt = string.trim(matches[6])
for key,value in pairs(xaos.quests) do if value.name==tempQuest then table.insert(xaos.quests[key].objectives, { name=name, complete=(comp=="X"), current=curcnt, max=maxcnt, kill=kill, }) end end
