function retrieveprofiles() table.load(getMudletHomeDir() .. "/characterprofiles.dat",xaos_profile) end
function loadprofile() copyfromprofile()
if xaos.config.drinkmode ~= "" then enableTimer("drinkTimer") end end
function copyfromprofile(onlogin)
local profilematch = table.n_collect(xaos_profile, function (elem) return elem.charactername == xaos.charactername end) if table.size(profilematch) > 0 then local curprofile = profilematch[1] xaos.config = table.deepcopy(curprofile.config) else echo("No Profile for " .. xaos.charactername) if onlogin then --Generate Blank Profile newprofile() end end end
function copytoprofile()
local profilematch = table.n_collect(xaos_profile, function (elem) return elem.charactername == xaos.charactername end) if table.size(profilematch) > 0 then local curprofile = profilematch[1] curprofile.config = table.deepcopy(xaos.config) else local newprofile = {charactername=xaos.charactername} newprofile.config = table.deepcopy(xaos.config) table.insert(xaos_profile,newprofile) echo("Profile Created For " .. xaos.charactername) end
table.save(getMudletHomeDir() .. "/characterprofiles.dat",xaos_profile) end
