Timer.script:
-- Calculate hp Delta
hpDelta = (current_health - lastHP)



--Do some math with hp Delta and store it
if math.abs(hpDelta) > hpDeltaMax and math.abs(hpDelta) < 25 then
  hpDeltaMax = math.abs(hpDelta)
end
--Figure out the general inertia of your HP flow by averaging
hpFlow = ((hpDelta4 + hpDelta3 + hpDelta2 + hpDelta) / 4)
-- cecho("\nthe hpFlow variable is defined as "..hpFlow)
if hpFlow < 0 then
  hpFlowBar:setValue(math.abs(hpFlow), 15, nil)
  hpFlowBar.front:setStyleSheet(
    [[background-color: red;
    border-top: 1px black solid;
    border-left: 1px black solid;
    border-bottom: 1px black solid;
    border-radius: 7;
    padding: 3px;
		]]
  )
elseif hpFlow > -1 then
  hpFlowBar:setValue(math.abs(hpFlow), 15, nil)
  hpFlowBar.front:setStyleSheet(
    [[background-color: green;
    border-top: 1px black solid;
    border-left: 1px black solid;
    border-bottom: 1px black solid;
    border-radius: 7;
    padding: 3px;
		]]
  )
end
lastHP = current_health
hpDelta4 = hpDelta3
hpDelta3 = hpDelta2
hpDelta2 = hpDelta

if current_health < 20 and auto_hunt then
	send("bclear;hero recall")
end

