function reloadUI() --createChatConsole() OOCCon:resetAutoWrap() questStatusCon:resetAutoWrap() dataCon:resetAutoWrap() OOCCon:resetAutoWrap()
local width,height = calcFontSize(getFontSize("main")) local windowWidth, windowHeight = getMainWindowSize() windowWidth = windowWidth - getBorderLeft() - getBorderRight() local wrap = math.floor(windowWidth/width) setWindowWrap("main", wrap)
mainWindowWrap = wrap end
