function xaos.class.isclass(classname) 
  return xaos.class.primary == classname or xaos.class.reborn == classname
end
function xaos.class.canberserk()
  return table.contains(xaos.skills,"berserk")
end
function xaos.class.cancenter()
  return table.contains(xaos.skills,"discipline")
end
function xaos.class.cantrance()
  return table.contains(xaos.skills,"trance")
end
function xaos.class.canresolve()
  return table.contains(xaos.skills,"resolve")
end
function xaos.class.canwarcry()
  return table.contains(xaos.skills,"warcry")
end
function xaos.class.canbless()
  return table.contains(xaos.spells,"bless")
end

function checkConfigDefaults() if xaos.config == nil then xaos.config = {} end if xaos.config.afkmode == nil then xaos.config.afkmode = false end if xaos.config.berserkovercenter == nil then xaos.config.berserkovercenter = true end if xaos.config.debugmode == nil then xaos.config.debugmode = false end if xaos.config.show_killed == nil then xaos.config.show_killed = false end if xaos.config.betamode == nil then xaos.config.betamode = false end if xaos.config.autologin == nil then xaos.config.autologin = false end if xaos.config.autologinaction == nil then xaos.config.autologinaction = "" end if xaos.config.reconnectaction == nil then xaos.config.reconnectaction = "" end if xaos.config.beeptell == nil then xaos.config.beeptell = false end if xaos.config.buffmode == nil then xaos.config.buffmode = true end if xaos.config.autojoin == nil then xaos.config.autojoin = true end if xaos.config.autoguard == nil then xaos.config.autoguard = false end if xaos.config.customDrink == nil then xaos.config.customDrink = "" end if xaos.config.drinkMode == nil then xaos.config.drinkMode = "soup" end if xaos.config.berserkmode == nil then xaos.config.berserkmode = true end if xaos.config.scripting == nil then xaos.config.scripting = {} end if xaos.config.scripting.findmode == nil then xaos.config.scripting.findmode = false end if xaos.config.scripting.scan_mode == nil then xaos.config.scripting.scan_mode = false end if xaos.config.scripting.huntwait == nil then xaos.config.scripting.huntwait = 1 end if xaos.config.scripting.endcombatwait == nil then xaos.config.scripting.endcombatwait = 2 end if xaos.config.scripting.maxmobs == nil then xaos.config.scripting.maxmobs = 10 end if xaos.config.scripting.autoLoot == nil then xaos.config.scripting.autoLoot = "" end if xaos.config.scripting.autoPickup == nil then xaos.config.scripting.autoPickup = "" end if xaos.config.scripting.autoDeathAction == nil then xaos.config.scripting.autoDeathAction = "" end if xaos.config.scripting.initialcommand == nil then xaos.config.scripting.initialcommand = "" end
if xaos.config.equipmentsets == nil then xaos.config.equipmentsets = {} end
if xaos.config.show_race == nil then xaos.config.show_race = false end end checkConfigDefaults()

