  menu.helpbutton = Geyser.Label:new({
    name="menu.helpbutton",
    x = "-" .. buttonoffset + 100 .. "px", y = "0px",
    width = "100px",
    height = "30px",
  })
  menu.helpbutton:setStyleSheet([[
      background-color: ]]..menu.color1..[[;
