    ]])
  menu.helpbutton:echo("<center>Help</center>")

  menu.helpbutton:setClickCallback("menu_helpbutton_click",v)

  function menu_helpbutton_click()
    helpWin.displaylabel:clear()
    echoHelpToConsole("","helpdisplaylabellabel")

    helpWin:show()
  end
  buttonoffset = buttonoffset + 110

  menu.scriptbutton = Geyser.Label:new({
    name="menu.scriptbutton",
    x = "-" .. buttonoffset + 100 .. "px", y = "0px",
    width = "100px",
    height = "30px",
  })
  menu.scriptbutton:setStyleSheet([[
      background-color: ]]..menu.color1..[[;
      border: 1px solid white;
    ]])
  menu.scriptbutton:echo("<center>Start Script</center>")

  menu.scriptbutton:setClickCallback("menu_scriptbutton_click",v)

  function menu_scriptbutton_click()
    if auto_hunt then
      auto_hunt = false
      xuicom("You will no longer attempt to automatically hunt targets")

      combat = false
      hunting = false
      menu.scriptbutton:echo("<center>Start Script</center>")
    else
      expandAlias("startscripting")
      menu.scriptbutton:echo("<center>Stop Script</center>")
    end
  end
  buttonoffset = buttonoffset + 110

  menu.container = Geyser.Container:new({
    name = "menu.backcontainer",
    x = "0%", y = "0%",
    width = "100%",
    height = "100%",
  },menu.window)

  menu.header = Geyser.VBox:new({
    name = "menu.header",
    x = "0%", y = "0%",
    width = "15%",
    height = "100%",
  },menu.container)
  menu.footer = Geyser.Label:new({
    name = "menu.footer",
    x = "15%", y = "0%",
    width = "85%",
    height = "100%",
  },menu.container)

  menu.footer:setStyleSheet([[
    background-color: black;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
  ]])

  menu.center = Geyser.Label:new({
    name = "menu.center",
    x = 0, y = 0,
    width = "100%",
    height = "100%",
  },menu.footer)
  menu.center:setStyleSheet([[
    border-radius: 10px;
    margin: 1px;
  ]])


end

function hideMenu()
  menu.window.visible = false
  menu.container:hide()
  resizeParent()
  mMenuHDrag:hide()
  menu.displaybutton:echo("<center>Sidebar</center>")
end

function showMenu()
  menu.window.visible = true
  menu.container:show()
  resizeParent()
  mMenuHDrag:show()
  menu.displaybutton:echo("<center>Sidebar</center>")
end

