function savegearset(name) local gear = table.deepcopy(xaos.currentequip) local filtered = table.n_filter(xaos.config.equipmentsets, function(elem) return elem.name == string.trim(matches[2]) end)
if table.size(filtered) == 0 then table.insert(xaos.config.equipmentsets, {name=string.trim(matches[2]), gear = gear} ) else filtered[1].gear = gear end end function weargearset(name)
local filtered = table.n_filter(xaos.config.equipmentsets, function(elem) return elem.name == string.trim(matches[2]) end)
if table.size(filtered) == 0 then echo("No set found with that name") elseif table.size(filtered) > 1 then echo("Too many sets found with that name") else
for k,v in pairs(currentequipment) do if v.location == "worn on finger" or v.location=="worn on wrist" or v.location=="worn on neck" or "held in left hand" then send("remove '" .. v.item .."") end end local gearset = filtered[1].gear local floatcount = 0 for k,v in pairs(gearset) do --held in right hand --held in left hand --held in both hands echo (v.location) if v.location == "held in right hand" or v.location=="held with both hands" then send("wield " .. v.item .."") elseif v.location == "held in left hand" then send("hold " .. v.item .."") elseif v.location == "floating nearby" then if floatcount == 0 then send("wear '" .. v.item .."' float") else send("kinetic '" .. v.item) end floatcount=floatcount+1 else local temploc = string.gsub(v.location,"worn on ","") temploc = string.gsub(temploc,"worn about ","") temploc = string.gsub(temploc,"worn around ","") send("wear '" .. v.item .."' " .. temploc) end end --echo("Dont forget your kinetics!") end end

function disableMSDP() local timer = 0 for k,v in pairs(msdp.REPORTABLE_VARIABLES) do tempTimer(timer,function() sendMSDP("UNREPORT",v) end) timer = timer + 0.01 end tempTimer(timer, function () sendMSDP("LIST","REPORTED_VARIABLES") echo("MSDP Unreporting Complete: " .. timer) end) end function enableMSDP() local timer = 0 for k,v in pairs(msdp.REPORTABLE_VARIABLES) do tempTimer(timer,function() sendMSDP("REPORT",v) end) timer = timer + 0.01 end tempTimer(timer, function () sendMSDP("LIST","REPORTED_VARIABLES") echo("MSDP Reporting Complete: " .. timer) end) end function reportMSDP() -- ask for a list of commands, lists, and reportable variables that the server supports sendMSDP("LIST", "COMMANDS", "LISTS", "REPORTABLE_VARIABLES") sendMSDP("LIST", "COMMANDS", "LISTS", "REPORTED_VARIABLES") end
function msdpConnect(event, arg, profile) reportMSDP() tempTimer(1,function() enableMSDP() end) end
msdpevents = {}
function registerMSDPEvents()
echo("Registering Event Handler") -- Register the event handler. msdpevents.xaos_connect = registerAnonymousEventHandler("xaos_connect", "msdpConnect")
end
function unregisterMSDPEvents()
if msdpevents.xaos_connect ~= nil then killAnonymousEventHandler(msdpevents.xaos_connect) end
end

function switchMSDPsidepanel()

    if mysettings.uicomponents.partybar == "left" then
      msdpPartyDisplay:changeContainer(left_container)
      msdpOppDisplay:changeContainer(left_container)
      msdpOppExtras:changeContainer(left_container)
      msdpPartyExtras:changeContainer(left_container)

      msdpPartyDisplay:show()
      msdpOppDisplay:show()

      left_container:resize("150px",getDimensions("main").height-130 .. "px")
      left_container:show()

    elseif mysettings.uicomponents.partybar == "right" then
      msdpPartyDisplay:changeContainer(right_container)
      msdpOppDisplay:changeContainer(right_container)
      msdpOppExtras:changeContainer(right_container)
      msdpPartyExtras:changeContainer(right_container)

      msdpPartyDisplay:show()
      msdpOppDisplay:show()

      right_container:resize("150px",getDimensions("main").height-130 .. "px")
      right_container:show()
    else
      msdpPartyDisplay:hide()
      msdpOppDisplay:hide()

      msdpPartyDisplay:changeContainer("main")
      msdpOppDisplay:changeContainer("main")
      msdpOppExtras:changeContainer("main")
      msdpPartyExtras:changeContainer("main")


    end
    if table.size(right_container.windows) == 1 then
      right_container:hide()
    end
    if table.size(left_container.windows) == 1 then
      left_container:hide()
    end
    resizeParent()
end

function loadMSDPUI()
  if msdpUIloaded == nil or not msdpUIloaded then
    msdpUIloaded = true
    partyDisplay:hide()
    partyDisplay2:hide()

    hpbar:move("0%","45%")
    manabar:move("30%","45%")
    mvmbar:move("60%","45%")
    ascbar:move("0%","87.5%")

    hpbar:resize("29.5%","40%")
    manabar:resize("29.5%","40%")
    mvmbar:resize("29.5%","40%")
    ascbar:resize("89.5%","10%")

    local sidecont
    if mysettings.uicomponents.partybar == "left" then
      sidecont = left_container
    else
      sidecont = right_container
    end


    msdpPartyDisplay = msdpPartyDisplay or Geyser.VBox:new({
      name = "msdpPartyDisplay",
      x = "5%", y = "5%",
      width = "90%", height = "40%",
    }, sidecont)

    msdpOppDisplay = msdpOppDisplay or Geyser.VBox:new({
      name = "msdpOppDisplay",
      x = "5%", y = "50%",
      width = "90%", height = "45%",
    }, sidecont)

    msdpTargetDisplay = msdpTargetDisplay or Geyser.VBox:new({
      name = "msdpTargetDisplay",
      x = "68%", y = "0%",
      width = "30%", height = "90%",
    }, bottom_container)


    msdpOppExtras = msdpOppExtras or Geyser.VBox:new({
      name = "msdpOppExtras",
      x = "2%", y = "45%",
      width = "95%", height = "40%",
    }, sidecont)
    msdpOppExtras:hide()

    msdpPartyExtras = msdpPartyExtras or Geyser.VBox:new({
      name = "msdpPartyExtras",
      x = "2%", y = "0",
      width = "96%", height = "40%",
    }, sidecont)
    msdpPartyExtras:hide()

    OPPGauges = OPPGauges or {} 
    --OPPLabels = OPPLabels or {}

    OPPGauges[1] = OPPGauges[1] or
          Geyser.Gauge:new({name = "OPPGauges_"..index,  x = "10%", y = "1%", width = "69.5%", height ="40%"}, gauge_container)


    PartyGauges = PartyGauges or {}
    PartyManaGauges = PartyManaGauges or {} 
    --PartyLabels = PartyLabels or {}


  end
end

function grabOppGauge(index,targetme)
      local cont,contheight
      if index== 1 then 
        cont = bottom_container
        contheight = 25
      else
        cont = msdpOppDisplay
        contheight = 10
      end

      OPPGauges[index] = OPPGauges[index] or
          Geyser.Gauge:new({name = "OPPGauges_"..index,  x = "0%", y = "0%", width = "100%", height ="100%"}, cont)

      if not targetme then
        OPPGauges[index].front:setStyleSheet(
          [[background-color: rgba(0,159,255,100%);
            border-width: 1px;
            border-color: black;
            border-style: solid;
            border-radius: 7;
            padding: 3px;
        ]]
        )
        OPPGauges[index].back:setStyleSheet(
          [[background-color: rgba(0,159,255,50%);
            border-width: 1px;
            border-color: black;
            border-style: solid;
            border-radius: 7;
            padding: 3px;
        ]]
        )
      else
        OPPGauges[index].front:setStyleSheet(
          [[background-color: rgba(255, 0, 0,100%);
            border-width: 1px;
            border-color: black;
            border-style: solid;
            border-radius: 7;
            padding: 3px;
        ]]
        )
        OPPGauges[index].back:setStyleSheet(
          [[background-color: rgba(255, 0, 0,50%);
            border-width: 1px;
            border-color: black;
            border-style: solid;
            border-radius: 7;
            padding: 3px;
        ]]
        )
      end

      if index > 1 then 
        OPPGauges[index]:changeContainer(cont)
        msdpOppDisplay:show()
      end

      --math.min(table.size(msdpOppDisplay.windows),5)*40 .. "px"
      msdpOppDisplay:resize("90%",math.min(table.size(msdpOppDisplay.windows),9)*5 .. "%")
      msdpOppDisplay:organize()
      OPPGauges[index]:reposition()

      return {gauge=OPPGauges[index]}
    end
  function hideOppGauges(index)
    local i
    if index==1 then 
      OPPGauges[index]:hide()
      index = 2 
    end

    for i=index,table.size(OPPGauges) do
      OPPGauges[i]:changeContainer(msdpOppExtras)
    end
  end
  function grabPartyGauge(index)
      local cont,contheight

      cont = msdpPartyDisplay
      contheight = 10


      PartyGauges[index] = PartyGauges[index] or
          Geyser.Gauge:new({name = "PartyGauges_"..index,  x = "1%", y = 0, width = "98%", height =contheight,v_stretch_factor=2.0,h_stretch_factor=2.0}, cont)
      PartyGauges[index].front:setStyleSheet(
        [[background-color: rgba(0, 255, 0,10%);
          border-width: 1px;
          border-color: black;
          border-style: solid;
          border-radius: 7;
          padding: 3px;
      ]]
      )
      PartyGauges[index].back:setStyleSheet(
        [[background-color: rgba(0, 255, 0,30%);
          border-width: 1px;
          border-color: black;
          border-style: solid;
          border-radius: 7;
          padding: 3px;
      ]]
      )
      PartyManaGauges[index] = PartyManaGauges[index] or
          Geyser.Gauge:new({name = "PartyManaGauges_"..index,  x = "1%", y = 0, width = "98%", height =contheight}, cont)
      PartyManaGauges[index].front:setStyleSheet(
        [[background-color: rgba(0, 0, 255,100%);
          border-width: 1px;
          border-color: black;
          border-style: solid;
          border-radius: 7;
          padding: 3px;
      ]]
      )
      PartyManaGauges[index].back:setStyleSheet(
        [[background-color: rgba(0, 0, 255,50%);
          border-width: 1px;
          border-color: black;
          border-style: solid;
          border-radius: 7;
          padding: 3px;
      ]]
      )
     msdpPartyDisplay:resize("95%",math.min((table.size(PartyGauges)),5)*40 .. "px")
     msdpPartyDisplay:organize()
     PartyGauges[index]:changeContainer(cont)
     PartyManaGauges[index]:changeContainer(cont)
     PartyGauges[index]:show()
     PartyManaGauges[index]:show()

     msdpPartyDisplay:show()

     return {gauge=PartyGauges[index],managauge=PartyManaGauges[index]}
    end
  function hidePartyGauges(index)
    local i

    for i=index,table.size(PartyGauges) do
      PartyGauges[i]:changeContainer(msdpPartyExtras)
      PartyManaGauges[i]:changeContainer(msdpPartyExtras)
    end
  end

function grab_EQ_WEIGHT_MAX()
  msdpUpdateEQ()
end

function grab_EQ_WEIGHT()

  msdpUpdateEQ()

end

function grab_EXPERIENCE()
if msdp.HEALTH == nil or msdp.HEALTH_MAX == nil then return end
xPrompt.exp = math.floor(msdp.EXPERIENCE/1000) current_xp = xPrompt.exp end

function grab_FRAGS()
  xaos.currency.frags=tonumber(msdp.FRAGS)
end

function grab_HERO_LEVEL()
  xaos.Level = tonumber(msdp.HERO_LEVEL)
end

function grab_HERO_POINTS()
  xaos.heropoints=tonumber(msdp.HERO_POINTS)  
end

function grab_ITEMS_MAX()
  msdpUpdateEQ()
end

function grab_ITEMS()
  msdpUpdateEQ()
end

function grab_SOULS()
  xaos.currency.souls=tonumber(msdp.SOULS)
end

function grab_SPELLS()
  xaos.spells = {}
  for k,v in pairs(msdp.SPELLS) do
    table.insert(xaos.spells,v)
  end
end

function grab_SKILLS()
  xaos.skills = {}
  for k,v in pairs(msdp.SKILLS) do
    table.insert(xaos.skills,v)
  end
end

function grab_TACTICS()
  xaos.tactics = string.trim(msdp.TACTICS)
end

function grab_TRAITS()
  xaos.traits = string.trim(msdp.TRAITS)
end
